# And the contract address
address = web3.toChecksumAddress('0xbc4ca0eda7647a8ab7c2061c2e118a18a936f13d')

# Representation of the contract. web3.eth.Contract(), two arguments: one for the smart contract ABI and one for the smart contract address.
contract = web3.eth.contract(abi=abi, address=address)

# Step 1: Get all token IDs using the totalSupply() + tokenByIndex(uint256 index) functions

totalSupply = contract.functions.totalSupply().call(block_identifier=15882990)
boredApeYachtIds = set();
for indexSupply in range(totalSupply):
	boredApeYachtIds.add(contract.functions.tokenByIndex(indexSupply).call(block_identifier=15882990))

# Step 2: Get the owners of the token using the ownerOf(uint256 index) function
hasBoredApeYacht = set()
for indexTokens in boredApeYachtIds:
	hasBoredApeYacht.add(contract.functions.ownerOf(indexTokens).call(block_identifier=15882990))
	
