# Step 3: Save as CSV

self.data = defaultdict(hasBoredApeYacht)

headers = ["NFT_id", "Address"]
new_df = pd.DataFrame(columns=headers)

new_df["NFT_id"] = self.data[0]
new_df["Address"] = self.data[1]

new_df.to_csv(r"./CSV/NFTOwners.csv", index=False)


